                $article->setImgUri($aResult->img_uri);
                $article->setNewsonfire(true);
                $article->setShowInterval($date);
                $article->setSocialScore($aResult->social_score);
                $article->setUrl($aResult->url);
                $article->setSocialSpeedSph($aResult->social_speed_sph);
                $article->setCategories($categories);
                $article->setCauses($causes);
                $article->setGravite($gravite);
                $all_departement = $manager->getRepository(Departement::class)->findAll();
                $added_departement= array();
                foreach ($all_departement as $departement) {
                    if ( (strpos($html_content, $departement->getName()) !== false) ) {
                        if(!in_array($departement->getName(),$added_departement)) {
                            array_push($added_departement, $departement->getName());
                            $article->addDepartement($departement);
                            $article->addRegion($departement->getRegion());
                        }
                    }
                }
                $manager->persist($article);
            }
        }

        $manager->flush();
    }

